import { VAlert } from 'vuetify/lib';
export default {
  components: {
    VAlert: VAlert
  },
  props: {
    success: {
      type: Boolean,
      "default": null
    },
    warning: {
      type: Boolean,
      "default": null
    },
    error: {
      type: Boolean,
      "default": null
    },
    dense: {
      type: Boolean,
      "default": null
    }
  },
  computed: {
    icon: function icon() {
      if (this.success) {
        return 'mdi-check-circle';
      }
      if (this.warning) {
        return 'mdi-alert';
      }
      if (this.error) {
        return 'mdi-alert-circle';
      }
      return 'mdi-information';
    },
    type: function type() {
      if (this.success) {
        return 'success';
      }
      if (this.warning) {
        return 'warning';
      }
      if (this.error) {
        return 'error';
      }
      return 'info';
    }
  }
};
