import { VTextarea } from 'vuetify/lib'

export default {
  extends: VTextarea,
  props: {
    // a more compact field
    dense: {
      type: Boolean,
      default: true,
    },
    // shows a border around field
    outlined: {
      type: Boolean,
      default: true,
    },
    // hides some extra space under the field which displays hints or errors
    hideDetails: {
      type: Boolean,
      default: true,
    },
  },
}
