import { protocolOptions, isOperatorOptions, allOperatorOptions } from '../../constants'

export const conditionDefs = {
  CLIENT_ADDRESS: { ops: isOperatorOptions, rules: 'required|ip_expression' },
  CLIENT_PORT: { ops: allOperatorOptions, rules: 'required|port' },
  SERVER_ADDRESS: { ops: isOperatorOptions, rules: 'required|ip_expression' },
  SERVER_PORT: { ops: allOperatorOptions, rules: 'required|port' },
  PORT_PROTOCOL: { ops: isOperatorOptions, autocompleteItems: protocolOptions, rules: 'required' },
}

export const conditionTypeHeaders = [
  { prefix: 'CLIENT', text: 'CLIENT' },
  { prefix: 'SERVER', text: 'SERVER' },
]

export const conditionTypeOptions = Object.keys(conditionDefs)
