<template>
  <v-app>
    <p>TEST CODE HERE</p>
    <p><u-btn color="primary" @click="toggleTheme">Toggle Theme</u-btn></p>
    <p><u-btn color="primary" @click="testSentryException">Test Sentry exception</u-btn></p>
    <u-gmap style="width: 500px; height: 300px" />
  </v-app>
</template>
<script>
  import { VApp } from 'vuetify/lib'
  import { captureException } from '@sentry/vue'
  import { UBtn, UGmap } from '../src/main'
  export default {
    components: { VApp, UBtn, UGmap },
    created() {
      // eslint-disable-next-line no-console
      console.log('created successfully')
    },
    methods: {
      toggleTheme() {
        this.$vuetify.theme.dark = !this.$vuetify.theme.dark
        this.$vuntangle.theme = this.$vuntangle.theme === 'dark' ? 'light' : 'dark'
      },
      testSentryException() {
        captureException(new Error(`test sentry tracing from vuntangle: ${Date.now()}`))
      },
    },
  }
</script>
