import Vue from 'vue'
import Vuetify from 'vuetify'
import { loadGmapApi } from 'gmap-vue'
import Vuntangle, { vuntangleTheme } from '../src/main'
import App from './App'

// install plugins
Vue.use(Vuetify)
Vue.use(Vuntangle, { env: 'development', sentryDsn: process.env.VUE_APP_SENTRY_DSN })

// create and inject plugin instances
const vuetify = new Vuetify(vuntangleTheme)
const vuntangle = new Vuntangle()

// hard code gmap key for testing
loadGmapApi({ key: 'AIzaSyAf2vwDAwiYgTO-36OoUX5hROI1lK-YKek' })

new Vue({
  vuetify,
  vuntangle,
  render: h => h(App),
}).$mount('#app')
