import { VAutocomplete } from 'vuetify/lib'

export default {
  extends: VAutocomplete,
  props: {
    // a more compact field
    dense: {
      type: Boolean,
      default: true,
    },
    // shows a border around field
    outlined: {
      type: Boolean,
      default: true,
    },
    // hides some extra space under the field which displays hints or errors
    hideDetails: {
      type: Boolean,
      default: true,
    },
    // selects the item highlighted when changing input fields with tab key
    // doesn't affect mouse navigation, does nothing if multiple is selected
    autoSelectFirst: {
      type: Boolean,
      default: true,
    },
  },
}
