<template>
  <v-overlay
    v-if="fetching || noData"
    absolute
    :color="$vuntangle.theme === 'dark' ? 'rgb(18, 18, 18)' : '#FFF'"
    :opacity="fetching ? 0.66 : 1"
    z-index="1"
  >
    <v-progress-circular v-if="fetching" indeterminate size="32" color="primary"></v-progress-circular>
    <div v-if="!fetching && noData" :style="`color: ${$vuntangle.theme === 'dark' ? '#FFF' : '#333'}`">
      {{ $vuntangle.$t('no_data_available') }}
    </div>
  </v-overlay>
</template>
<script>
  import { VOverlay, VProgressCircular } from 'vuetify/lib'

  export default {
    components: { VOverlay, VProgressCircular },
    props: {
      fetching: { type: Boolean, default: true },
      noData: { type: Boolean, default: false },
    },
  }
</script>
