<template>
  <div>
    <u-text-field
      ref="input"
      v-model="text"
      class="text-body-1"
      :height="20"
      :outlined="false"
      :placeholder="`${$vuntangle.$t('filter')}...`"
      @input="filter()"
    />
  </div>
</template>
<script>
  import Vue from 'vue'

  export default Vue.extend({
    data() {
      return {
        text: null,
      }
    },
    methods: {
      filter() {
        if (this.text === '') {
          // Remove the filter
          this.params.parentFilterInstance(instance => {
            instance.setModel({ value: null })
          })
          return
        }

        this.params.parentFilterInstance(instance => {
          instance.setModel({ value: this.text })
        })
      },

      onParentModelChanged(parentModel) {
        // When the filter is empty we will receive a null value here
        if (!parentModel) {
          this.text = ''
        } else {
          this.text = parentModel.value
        }
      },
    },
  })
</script>
