<template>
  <div
    class="network-interface"
    :style="{ background: $vuntangle.theme === 'dark' ? $vuetify.theme.currentTheme.darkGray : '#f5f5f5' }"
  >
    <div class="d-flex mt-1">
      <i :class="`mdi mdi-${icon} mr-1`" style="height: 16px; margin-top: 2px" />
      <div>
        <router-link class="name" :to="`/settings/network/interfaces/${networkInterface.device}`">
          <b>{{ networkInterface.name }}</b>
        </router-link>
      </div>
    </div>
    <v-tooltip v-if="multipleIps" right transition="false">
      <template #activator="{ on, attrs }">
        <p class="ip" v-bind="attrs" v-on="on">{{ networkInterface.ip4Addr[0] }} ...</p>
      </template>
      <span v-html="networkInterface.ip4Addr.join(', <br />')"></span>
    </v-tooltip>
    <p v-else-if="networkInterface.ip4Addr" class="ip">{{ networkInterface.ip4Addr[0] }}</p>
    <p class="rate" :style="$vuntangle.theme === 'dark' ? {} : { color: '#5c5c5c' }">
      <i class="mdi mdi-arrow-down" /><b>{{ networkInterface.wan ? rxByteRate : txByteRate }}</b>
      {{ $vuntangle.$t('kbps') }}<br />
      <i class="mdi mdi-arrow-up" /><b>{{ networkInterface.wan ? txByteRate : rxByteRate }}</b>
      {{ $vuntangle.$t('kbps') }}
    </p>
    <div :class="`connector${networkInterface.wan ? ' connector--top' : ''}`" />
    <div class="status">
      <i class="mdi mdi-circle" :style="{ color: $vuetify.theme.currentTheme[statusColor] }" />
    </div>
  </div>
</template>
<script>
  import { VTooltip } from 'vuetify/lib'
  export default {
    components: { VTooltip },
    props: {
      networkInterface: { type: Object, required: true, default: () => {} },
    },
    computed: {
      multipleIps: ({ networkInterface }) =>
        Array.isArray(networkInterface.ip4Addr) && networkInterface.ip4Addr.length > 1,
      icon() {
        return this.networkInterface.type === 'WIFI'
          ? 'access-point-network'
          : this.networkInterface.type === 'WWAN'
          ? 'signal'
          : this.networkInterface.type === 'OPENVPN' || this.networkInterface.type === 'WIREGUARD'
          ? 'wan'
          : this.networkInterface.type === 'VLAN'
          ? 'lan'
          : 'network-outline'
      },
      rxByteRate() {
        return this.networkInterface.rxByteRate ? this.networkInterface.rxByteRate / 1000 : 0
      },
      txByteRate() {
        return this.networkInterface.txByteRate ? this.networkInterface.txByteRate / 1000 : 0
      },
      statusColor() {
        return !this.networkInterface.offline &&
          (this.networkInterface.connected ||
            this.networkInterface.type === 'WIFI' ||
            this.networkInterface.type === 'WWAN')
          ? 'utGreen'
          : this.$vuntangle.theme === 'dark'
          ? 'lightGray'
          : 'darkGray'
      },
    },
  }
</script>
<style lang="scss">
  @import '../../scss/variables';

  .network-interface {
    position: relative;
    box-shadow: 0 1px 3px rgb(0 0 0 / 10%);
    margin: 0 5px;
    padding: 8px;
    border-radius: 3px;
    text-align: left;
    border: 1px $utGreen solid;
    display: block;
    min-width: 100px;

    p {
      margin: 0;
    }

    .ip {
      font-size: 11px;
    }

    .rate {
      font-size: 11px;
      line-height: 1;
      margin-top: 3px;
      text-align: left;
    }

    .connector {
      position: absolute;
      width: 3px;
      background: $utGreen;
      height: 20px;
      margin-left: 50%;
      top: -21px;
      left: -1px;
      font-size: 20px;
      color: $utGreen;

      &--top {
        bottom: -21px;
        top: auto;
      }
    }

    .status {
      position: absolute;
      right: 5px;
      top: 5px;
      height: 10px;
      line-height: 10px;
      font-size: 12px;
    }

    .name {
      font-size: 12px;
      color: $utGreen;
    }
  }
</style>
