<template>
  <v-container :class="classes" :fluid="fullWidth">
    <!-- the page title -->
    <h1 class="headline" v-html="title" />
    <span v-if="subtitle" class="body-2" v-html="subtitle" />
    <v-divider class="mb-4 mt-2" />
    <!-- page actions slot displayed under the title -->
    <div v-if="$slots.actions" class="mb-4 d-flex ut-actions">
      <slot name="actions"> </slot>
    </div>

    <!-- page default slot containing the main content -->
    <slot></slot>
  </v-container>
</template>
<script>
  import { VContainer, VDivider } from 'vuetify/lib'

  export default {
    components: { VContainer, VDivider },
    props: {
      // page title
      title: {
        type: String,
        default: null,
      },
      // page subtitle
      subtitle: {
        type: String,
        default: null,
      },
      // boolean indicating if the page should fill entire horizontal space
      fullWidth: {
        type: Boolean,
        default: true,
      },
      /**
       * boolean indicating if the page should fill visible height
       * used mostly on full page grids
       */
      fullHeight: {
        type: Boolean,
        default: false,
      },
    },
    computed: {
      // page classes to accommodate passed props
      classes() {
        let classes = 'pa-4 '
        if (this.fullHeight) {
          classes += 'd-flex flex-column flex-nowrap fill-height align-stretch'
        }
        return classes
      },
    },
  }
</script>
<style>
  /** use a default right margin for spacing action buttons */
  .ut-actions .v-btn {
    margin-right: 16px;
  }
</style>
