<!--
  Component used mostly to group form fields in a section with a title
  Equivalent of the fieldset tag
-->
<template>
  <v-card outlined class="mb-4 pa-4 ut-section">
    <div v-if="title || $slots.trActions" class="d-flex mb-2">
      <u-section-title v-if="title">{{ title }}</u-section-title>
      <v-spacer />
      <slot name="tr-actions"> </slot>
    </div>
    <!-- default slot content -->
    <slot></slot>
    <div v-if="$slots.actions || $slots.brActions" class="d-flex mt-4">
      <slot name="actions"> </slot>
      <v-spacer />
      <slot name="br-actions"> </slot>
    </div>
  </v-card>
</template>
<script>
  import { VCard, VSpacer } from 'vuetify/lib'
  import USectionTitle from '../USectionTitle'

  export default {
    components: { VCard, VSpacer, USectionTitle },
    props: {
      title: {
        type: String,
        default: null,
      },
    },
  }
</script>
