<template>
  <v-sheet
    :outlined="outlined"
    :rounded="rounded"
    class="pa-4 ma-0"
    :color="sheetColor"
    :height="height"
    style="overflow-y: auto"
  >
    <slot></slot>
  </v-sheet>
</template>
<script>
  export default {
    props: {
      outlined: {
        type: Boolean,
        default: () => true,
      },
      rounded: {
        type: Boolean,
        default: () => true,
      },
      height: {
        type: Number,
        default: () => 500,
      },
    },
    computed: {
      sheetColor() {
        return this.$vuetify.theme.isDark ? 'grey darken-2' : 'grey lighten-2'
      },
    },
  }
</script>
