<template>
  <span>
    <v-icon small class="mr-1" :color="action.iconColor || ''">{{ action.icon }}</v-icon>
    <span class="font-weight-bold" @click.prevent.stop="onClick" v-html="html"> </span>
  </span>
</template>
<script>
  import Vue from 'vue'
  export default Vue.extend({
    data() {
      return {
        showTooltip: false,
      }
    },
    computed: {
      action: ({ params }) => params.value,
      html: ({ action, params }) => {
        if (action.id && params.click) return `<a id="${action.id}">${action.text}</a>`
        return action.text
      },
    },
    methods: {
      onClick(e) {
        if (!this.params.click) return
        if (e.target.id) this.params.click(e.target.id)
      },
    },
  })
</script>
