<template>
  <span class="font-weight-bold" @click.prevent.stop="onClick" v-html="html"> </span>
</template>

<script>
  import Vue from 'vue'

  export default Vue.extend({
    computed: {
      html: ({ params }) => {
        if (params.click) return params.value.map(policy => `<a id="${policy.id}">${policy.name}</a>`).join(', ')
        return params.value.map(policy => policy.name).join(', ')
      },
    },
    methods: {
      onClick(e) {
        if (!this.params.click) return
        if (e.target.id) this.params.click(e.target.id)
      },
    },
  })
</script>
