<template>
  <span v-if="Array.isArray(params.value)" style="word-break: break-word">
    <span v-for="(group, idx) in params.value" :key="idx">
      <span v-if="params.value.length > 1">(</span>
      <span v-for="(obj, idx2) in group" :key="idx2">
        <object-renderer :object="obj" />
        <span v-if="idx2 < group.length - 1" class="text-uppercase font-weight-bold"> {{ $t('or') }} </span>
      </span>
      <span v-if="params.value.length > 1">)</span>
      <span v-if="idx < params.value.length - 1" class="text-uppercase font-weight-bold"> {{ $t('and') }} </span>
    </span>
  </span>
  <span v-else>{{ params.value }}</span>
</template>
<script>
  import Vue from 'vue'
  import ObjectRenderer from './ObjectRenderer.vue'

  export default Vue.extend({
    components: { ObjectRenderer },
  })
</script>
