<template>
  <condition-items :items="items" :has-group-conditions="hasGroupConditions" />
</template>

<script>
  import Vue from 'vue'
  import ConditionItems from './ConditionItems.vue'

  export default Vue.extend({
    components: { ConditionItems },
    computed: {
      items: ({ params }) => params.value,
      hasGroupConditions: ({ params }) => !!params.data.group,
    },
  })
</script>
