<template>
  <span>
    <v-hover ref="chipRef" v-slot="{ hover }" v-model="showTooltip">
      <span class="font-weight-bold" style="white-space: nowrap">
        <v-icon dense :color="hover ? 'primary' : 'grey'">
          {{ object.isGroup ? 'mdi-label-multiple' : 'mdi-label' }}
        </v-icon>
        {{ object.name }}
      </span>
    </v-hover>

    <v-tooltip
      :value="showTooltip"
      bottom
      max-width="600"
      :activator="$refs.chipRef"
      transition="false"
      color="primary"
    >
      <span>{{ object.tooltip }}</span>
    </v-tooltip>
  </span>
</template>
<script>
  export default {
    props: {
      /**
       * an object having name & tooltip data
       * { name: '<object name>', tooltip: <generated_tooltip_string_or_object> } // object in case of groups
       */
      object: { type: Object, required: true },
    },
    data() {
      return {
        showTooltip: undefined,
      }
    },
  }
</script>
