import reports from '../../../plugins/reports'

export default {
  props: {
    view: { type: Object, default: undefined },
  },

  computed: {
    /**
     * Returns the reports configurations associated with any view
     * Each view is reponsible of handling this reports array
     * @param {Object} vm - vue instance
     * @param {View} vm.view - the report view passed via mixin
     * @returns {Array<Report>}
     */
    reports: ({ view }) => {
      const list = []
      view.reports.forEach(id => list.push(reports[id]))
      return list
    },
  },
}
